use lennujaam;

create table lennujaamtable (
	id INT,
	lennujaam_nimi VARCHAR(50),
	aeg VARCHAR(50),
	summa VARCHAR(50),
	lennujaam_asukoht VARCHAR(50),
	lennujaam_kood VARCHAR(50),
	lennujaam_riigi_kood VARCHAR(50),
	lennujaam_GPS_kood VARCHAR(50)
);


select 

lennujaam_nimi as '@lennujaam_nimi', 
lennujaam_nimi,
aeg,
summa as 'reis/summa',
lennujaam_asukoht,
lennujaam_kood,
lennujaam_riigi_kood,
lennujaam_GPS_kood from lennujaamtable for xml path('lennujaamtable'), ROOT ('lennujaam');
